mapper.select:
select course.* ,sys_user.nickname as teacher from course left join sys_user on course.teacher_id = sys_user.id
and name like concat('%',#{name},'%')
and credit = #{credit}
and attributes like concat('%',#{attributes},'%')
and teacher_id = #{id}

select course.*,sys_user.nickname as teacher from student_course left join course on student_course.course_id = course.id left join sys_user on course.teacher_id = sys_user.id
and name like concat('%',#{name},'%')
and credit = #{credit}
and attributes like concat('%',#{attributes},'%')
and student_id = #{id}
